package test

import (
	"strings"
	"testing"

	"github.com/stretchr/testify/assert"

	"github.com/chyroc/lark"
)

func Test_AI(t *testing.T) {
	as := assert.New(t)

	cli := AppAllPermission.Ins()
	moduleCli := cli.AI

	t.Run("", func(t *testing.T) {
		resp, response, err := moduleCli.DetectTextLanguage(ctx, &lark.DetectTextLanguageReq{
			Text: "Some checks haven’t completed yet",
		})
		printData(resp)
		as.Nil(err)
		as.NotNil(resp)
		as.Equal("en", strings.ToLower(resp.Language))
		as.NotEmpty(response.RequestID)
	})

	t.Run("", func(t *testing.T) {
		resp, response, err := moduleCli.TranslateText(ctx, &lark.TranslateTextReq{
			SourceLanguage: "zh",
			Text:           "国家",
			TargetLanguage: "en",
			Glossary:       nil,
		})
		printData(resp)
		as.Nil(err)
		as.NotNil(resp)
		as.Equal("country", strings.ToLower(resp.Text))
		as.NotEmpty(response.RequestID)
	})

	t.Run("", func(t *testing.T) {
		runImageText := ``
		resp, _, err := moduleCli.RecognizeBasicImage(ctx, &lark.RecognizeBasicImageReq{
			Image: &runImageText,
		})
		printData(resp)
		as.Nil(err)
		as.NotNil(resp)
		as.Len(resp.TextList, 1)
		as.Equal("Run", resp.TextList[0])
	})

	t.Run("", func(t *testing.T) {
		runImageText := ``
		resp, _, err := moduleCli.RecognizeBasicImage(ctx, &lark.RecognizeBasicImageReq{
			Image: &runImageText,
		})
		printData(resp)
		as.Nil(err)
		as.NotNil(resp)
		as.Len(resp.TextList, 1)
		as.Equal("Run", resp.TextList[0])
	})
}
